"use client";

import { Button } from "@/components/ui/button";

interface StepCardProps {
  stepNumber: string;
  icon: React.ReactNode;
  title: string;
  description: string;
}

function StepCard({ stepNumber, icon, title, description }: StepCardProps) {
  return (
    <div className="group relative">
      <div className="text-5xl font-semibold tracking-tight text-primary/60 sm:text-6xl">
        {stepNumber}
      </div>
      <div className="flex flex-col items-center text-center">
        <div className="mb-4 flex h-16 scale-100 items-center justify-center sm:scale-125 lg:scale-150">
          {icon}
        </div>
        <h3 className="mb-3 text-xl font-medium tracking-tight text-foreground sm:text-2xl">
          {title}
        </h3>
        <p className="max-w-sm text-base leading-relaxed text-muted-foreground sm:text-lg">
          {description}
        </p>
      </div>
    </div>
  );
}

export function HowItWorks() {
  const steps = [
    {
      stepNumber: "01",
      icon: <UploadDocumentSVG />,
      title: "Upload Document",
      description: "Easily upload your Show Cause Notice or Order.",
    },
    {
      stepNumber: "02",
      icon: <AIHighlightsSVG />,
      title: "AI Highlights",
      description: "AI instantly identifies and highlights key information.",
    },
    {
      stepNumber: "03",
      icon: <SelectIssuesSVG />,
      title: "Select Issues",
      description: "Choose the exact dispute points you want to address.",
    },
    {
      stepNumber: "04",
      icon: <FindReferencesSVG />,
      title: "Find References",
      description: "Get AI-suggested legal authorities and references.",
    },
    {
      stepNumber: "05",
      icon: <GenerateDraftSVG />,
      title: "Generate Draft",
      description: "AI creates a structured, ready-to-use appeal draft.",
    },
    {
      stepNumber: "06",
      icon: <ReviewAndExportSVG />,
      title: "Review & Export",
      description: "Polish the final version and export for submission.",
    },
  ];

  return (
    <section
      id="how-it-works"
      className="relative bg-background px-4 py-20 sm:px-6 lg:px-8"
    >
      <div className="mx-auto max-w-7xl">
        <div className="mb-16 text-center">
          <h2 className="mb-6 text-4xl font-semibold tracking-tighter text-foreground sm:text-5xl">
            How it Works?
          </h2>
          <p className="mx-auto max-w-3xl text-base leading-relaxed text-muted-foreground sm:text-lg">
            From uploading documents to generating polished drafts, ChatGST
            makes the entire process fast, accurate, and effortless with
            AI-powered automation.
          </p>
        </div>

        <div className="grid gap-12 sm:grid-cols-2 lg:grid-cols-3 lg:gap-8 xl:gap-12">
          {steps.map((step, index) => (
            <StepCard
              key={index}
              stepNumber={step.stepNumber}
              icon={step.icon}
              title={step.title}
              description={step.description}
            />
          ))}
        </div>

        <div className="mt-16 flex justify-center">
          <Button
            size="lg"
            className="rounded-lg bg-primary px-8 py-6 text-base font-medium text-primary-foreground hover:bg-primary/90"
          >
            Begin Appeal Draft
          </Button>
        </div>
      </div>
    </section>
  );
}

